<script>
    import LoadingIcon from '../SvgIcon/LoadingIcon.vue';

    export default {
        components: {
            LoadingIcon,
        },
        props: {
            show: {
                type: Boolean,
                required: true
            },
            response: {
                type: Object,
                required: true
            },
            answers: {
                type: Array,
            },
            language: {
                type: Object,
                required: false
            },
            job: {
                type: Object,
                required: false
            },
            messagebutton: {
                type: Boolean,
                required: true,
                default: true
            }
        },
        data() {
            return {
                show: false,
                data: {},
                social: {},
                candidate: {},
                languageTranslation: {},
                candidateMessageModal: false,
                message: '',
                msgloading: false,
            }
        },
        methods: {
            __(key) {
                if (this.languageTranslation) {
                    return this.languageTranslation[key] || key;
                }

                return key;
            },
            truncateText(text, words) {
                if (!text) return '';

                const wordArray = text.trim().split(' ');
                const truncatedText = wordArray.slice(0, words).join(' ');

                if (wordArray.length > words) {
                    return truncatedText + '...';
                }

                return truncatedText;
            },
            messageCandidate(){
                this.candidateMessageModal = true
            },
            async sendMessage(){
                if (!this.message) {
                    alert('Please write a message to send!');
                    return;
                }

                console.log(this.message)
                console.log(this.candidate.id)
                console.log(this.job.id)




                this.msgloading = true
                let response = await axios.post('/company/message/candidate', {
                    message: this.message,
                    candidate_id: this.candidate.id,
                    job_id: this.job.id
                })

                if (response.data.success) {
                    alert('Message sent successfully!')
                    this.candidateMessageModal = false
                    this.message = ''
                }else {
                    alert('Something went wrong while trying to send message! Please try again');
                }
                this.msgloading = false
            }
        },
        watch: {
            response() {
                this.data = this.response.data
                this.social = this.response.data.social_info
                this.candidate = this.response.data.candidate
            }
        },
        mounted() {
            this.data = this.response.data
            this.social = this.response.data.social_info
            this.candidate = this.response.data.candidate
            this.languageTranslation = this.language
        },
        computed: {
            sanitizedBio() {
                const parser = new DOMParser();
                const doc = parser.parseFromString(this.candidate.bio, 'text/html');
                return doc.body.textContent;
            }
        }
    }
</script>

